import React from "react";
import style from './Users.module.css'
import User from "./User";


let Users = (props) => {

    let pageCount = Math.ceil(props.totalPage / props.amountUser)
    let pages = []
    for (let i = 1; i < pageCount; i++) {
        pages.push(i)
    }
    return <div className={style.users}>
        <div className={style.pages}>
            {
                pages.map(el => {
                    return <span
                        className={props.currentPage === el && style.activePage}
                        onClick={(e) => {
                            props.onPageChange(el)
                        }}>{el}</span>
                })
            }
        </div>
        {
            props.users.map(user => <User user = {user} followInProgress = {props.followInProgress} follow = {props.follow} unFollow = {props.unFollow}/>)
        }

    </div>
}

export default Users
